import React from "react";
import { View, Text, StyleSheet } from "react-native";
import axios from "axios";

function Card({ startDate, startMonth, leaveType, numberDays }) {
  return (
    <View style={styles.cardContainer}>
      <View style={styles.startDateContainer}>
        <Text style={styles.dateText}>{startDate}</Text>
        <Text style={styles.monthText}>{startMonth}</Text>
      </View>
      <View style={styles.leaveDetailsContainer}>
        <Text style={styles.typeText}>{leaveType}</Text>
        <Text style={styles.daysText}>{numberDays + " days"}</Text>
      </View>
    </View>
  );
}

export default Card;

const styles = StyleSheet.create({
  cardContainer: {
    borderRadius: 10,
    marginHorizontal: 10,
    flexDirection: "row",
    height: 80,
    marginVertical: 10,
    backgroundColor: "#eaeaea",
    padding: 10,
    alignItems: "center",
  },
  startDateContainer: {
    borderWidth: 2,
    borderColor: "#5fcbf9",
    borderRadius: 8,
    alignItems: "center",
    paddingVertical: 4,
    paddingHorizontal: 8,
  },
  dateText: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#5fcbf9",
  },
  monthText: {
    fontSize: 12,
    color: "#5fcbf9",
  },
  leaveDetailsContainer: {
    flex: 1,
    marginLeft: 10,
  },
  typeText: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#333333",
  },
  daysText: {
    fontSize: 14,
    color: "#666666",
  },
});
