import React, { useState, useEffect } from "react";
import {
  View,
  Modal,
  Alert,
  StyleSheet,
  TouchableOpacity,
  Text,
  FlatList,
} from "react-native";
import { Calendar } from "react-native-calendars";
import { Ionicons } from "@expo/vector-icons";
import AddButton from "../../components/AddButton";
import LeaveRequestForm from "../../components/form";
import Card from "../../components/card";
import axios from "axios";

const MyLeavesScreen = ({ navigation }) => {
  const [selectedStartDate, setSelectedStartDate] = useState("");
  const [selectedEndDate, setSelectedEndDate] = useState("");
  const [selectedDays, setSelectedDays] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [leaveData, setLeaveData] = useState([]);
  const [selected, setSelected] = useState(false);
  const [selectedStatus, setSelectedStatus] = useState("all");
  const months = Array.from({ length: 12 }, (_, monthIndex) => {
    const date = new Date(0, monthIndex);
    return date.toLocaleString("en-US", { month: "short" });
  });

  useEffect(() => {
    fetchLeaveData();
  }, []);

  const fetchLeaveData = async () => {
    try {
      const response = await axios.get("http://10.0.2.2:5000/leaverequest");
      const data = response.data;
      setLeaveData(data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleDayPress = (day) => {
    const { dateString } = day;

    const updatedSelectedDays = { ...selectedDays };

    if (selectedDays[dateString]) {
      delete updatedSelectedDays[dateString];
    } else {
      updatedSelectedDays[dateString] = { selected: true };
    }

    const sortedSelectedDates = Object.keys(updatedSelectedDays).sort();

    setSelectedStartDate(sortedSelectedDates[0]);
    setSelectedEndDate(sortedSelectedDates.slice(-1)[0]);

    const daysBetween = getDaysBetween(
      sortedSelectedDates[0],
      sortedSelectedDates.slice(-1)[0]
    );
    daysBetween.forEach((date) => {
      updatedSelectedDays[date] = { selected: true };
    });

    setSelectedDays(updatedSelectedDays);
  };

  const getDaysBetween = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const daysBetween = [];

    for (let date = start; date <= end; date.setDate(date.getDate() + 1)) {
      daysBetween.push(new Date(date).toISOString().slice(0, 10));
    }

    return daysBetween;
  };
  function isSelected() {
    if (selectedDays) setSelected(true);
  }

  const handleAddLeave = () => {
    setShowModal(true);
  };

  function clearSelectedDates() {
    setSelectedDays({});
  }

  const handleSubmitLeave = (formData) => {
    const leaveData = {
      startDate: selectedStartDate,
      endDate: selectedEndDate,
      ...formData,
    };

    Alert.alert("Leave request submitted successfully");

    setShowModal(false);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleStatusFilter = (status) => {
    setSelectedStatus(status);
  };

  const filteredLeaveData =
    selectedStatus === "all"
      ? leaveData
      : leaveData.filter((data) => data.status === selectedStatus);

  React.useLayoutEffect(() => {
    navigation.setOptions({
      headerRight: () => (
        <TouchableOpacity onPress={handleAddLeave}>
          <Ionicons
            name="add-circle-outline"
            size={30}
            color="#5fcbf9"
            style={{ fontSize: 40, fontWeight: "100" }}
          />
        </TouchableOpacity>
      ),
    });
  }, [navigation]);

  const renderLeaveRequest = ({ item }) => (
    <Card
      startDate={item.startDate.slice(8)}
      leaveType={item.leaveType}
      startMonth={months[Number(item.startDate.slice(5, -3)) - 1]}
      numberDays={item.numberDays}
    />
  );

  return (
    <View style={styles.container}>
      <View style={styles.filterOptions}>
        <TouchableOpacity
          style={[
            styles.filterOption,
            selectedStatus === "all" && styles.selectedFilterOption,
          ]}
          onPress={() => handleStatusFilter("all")}
        >
          <Text style={styles.filterOptionText}>All</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.filterOption,
            selectedStatus === "pending" && styles.selectedFilterOption,
          ]}
          onPress={() => handleStatusFilter("pending")}
        >
          <Text style={styles.filterOptionText}>Pending</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.filterOption,
            selectedStatus === "accepted" && styles.selectedFilterOption,
          ]}
          onPress={() => handleStatusFilter("accepted")}
        >
          <Text style={styles.filterOptionText}>Accepted</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.filterOption,
            selectedStatus === "rejected" && styles.selectedFilterOption,
          ]}
          onPress={() => handleStatusFilter("rejected")}
        >
          <Text style={styles.filterOptionText}>Rejected</Text>
        </TouchableOpacity>
      </View>

      <FlatList
        data={filteredLeaveData}
        keyExtractor={(item, index) => index.toString()}
        renderItem={renderLeaveRequest}
      />

      <Modal
        visible={showModal}
        animationType="slide"
        transparent={true}
        onRequestClose={handleCloseModal}
      >
        <View style={styles.modalContainer}>
          <View style={styles.formContainer}>
            <LeaveRequestForm
              onSubmit={handleSubmitLeave}
              onCancel={handleCloseModal}
              endDateC={selectedEndDate}
              startDateC={selectedStartDate}
            />
          </View>

          <Calendar
            style={styles.calendar}
            onDayPress={handleDayPress}
            markedDates={selectedDays}
          />

          <AddButton onPress={clearSelectedDates} s>
            clear
          </AddButton>
        </View>
      </Modal>
    </View>
  );
};

export default MyLeavesScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  filterOptions: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingHorizontal: 20,
    paddingTop: 10,
    marginBottom: 10,
  },
  filterOption: {
    padding: 10,
    borderRadius: 5,
  },
  selectedFilterOption: {
    backgroundColor: "#5fcbf9",
  },
  filterOptionText: {
    color: "white",
    fontWeight: "bold",
  },
  calendar: {
    marginHorizontal: 30,
    marginVertical: 25,
    borderRadius: 10,
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",

    backgroundColor: "#ffffff",
  },
  formContainer: {
    backgroundColor: "#fff",
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
    alignItems: "center",
  },
});
